  //if (loading) return (<div>'Loading...'</div>);
  /*if (error) {
    console.warn(error);
    return (<div>`Error! ${error.message}`</div>);
  }*/

  // Cache results from our GraphQL query
  client.writeData({
    data: {
      searchStr: data.getPlayer.searchStr,
      name: data.getPlayer.name,
      id: data.getPlayer.id,
    }
  });

  // Render JSX element and insert data
  return (
    <div>
      <input placeholder="Enter Player's Name" value={data.getPlayer.searchStr} onChange={e=>e.preventDefault() }/>
      <br/>
      <h3>{data.getPlayer.name}</h3>
    </div>
  )
};

export default NameSearch;
