import React from "react";
import { gql } from 'apollo-boost';
import { useQuery, useLazyQuery, useApolloClient } from '@apollo/react-hooks';
import { typeDefs } from './BaseballApp';

const PLAYER_QUERY = gql`
  query Player($searchStr: String!){
    getPlayer(searchStr: $searchStr){
      searchStr
      name
      id
    }
  }
`;

type playerQueryObj = {
  searchStr: string,
  name: string,
  id: number
};

// NameSearch function for taking text entered and searching for closest player
// whose name matches the text
function NameSearch(): any {
  // Initialize ApolloClient so we can get access to ApolloProvider's cache
  const client = useApolloClient();

  // Use useLazyQuery() hook to pass in query server
  const [getPlayer, result] = useLazyQuery(PLAYER_QUERY);
  console.log(result.data);
  let name = '';
  let searchStr = ''

  //const { loading, error, data } = useQuery(PLAYER_QUERY, {variables: {searchStr: 'Marcus Se'}});

  // handle return and error states
  if (result.loading) return (<div>'Loading...'</div>);
  if (result.error) {
    console.warn(result.error);
    return (<div>`Error! ${result.error.message}`</div>);
  }

  // Cache results from our GraphQL query
  if (result.data) {
    name = result.data.getPlayer.name;
    let playerText:string = result.data.getPlayer.name
    console.log(playerText);

    client.writeData({
      data: {
        searchStr: result.data.getPlayer.searchStr,
        name: result.data.getPlayer.name,
        id: result.data.getPlayer.id,
      }
    });
  }

  // Render JSX element and insert data
  return (
    <div>
      <input placeholder="Enter Player's Name" value={data ? data.getPlayer.searchStr : ''} onChange={e => getPlayer({ variables: { searchStr: e.target.value }})  }/>
      <br/>
      <h3>{name}</h3>
    </div>
  )
};

export default NameSearch;
