import React from "react";
import { gql } from 'apollo-boost';
import { useQuery, useLazyQuery, useApolloClient } from '@apollo/react-hooks';
import { typeDefs } from './BaseballApp';

const PLAYER_QUERY = gql`
  query Player($searchStr: String!){
    getPlayer(searchStr: $searchStr){
      searchStr
      name
      id
    }
  }
`;

type playerQueryObj = {
  searchStr: string,
  name: string,
  id: number
};

// NameSearch function for taking text entered and searching for closest player
// whose name matches the text
function NameSearch(): any {
  // Initialize ApolloClient so we can get access to ApolloProvider's cache
  const client = useApolloClient();

  // Use useQuery() hook to pass in text values
  const [getPlayer, {data}] = useLazyQuery(PLAYER_QUERY);
  //getPlayer({ variables: { searchStr: 'Marcus Se' }});
  //console.log(loading);
  //console.log(error);
  console.log(data);
  let name = '';

  //const { loading, error, data } = useQuery(PLAYER_QUERY, {variables: {searchStr: 'Marcus Se'}});

  // handle return and error states
  //if (loading) return (<div>'Loading...'</div>);
  /*if (error) {
    console.warn(error);
    return (<div>`Error! ${error.message}`</div>);
  }*/

  // Cache results from our GraphQL query
  if (data) {
    name = data.getPlayer.name;

    client.writeData({
      data: {
        searchStr: data.getPlayer.searchStr,
        name: data.getPlayer.name,
        id: data.getPlayer.id,
      }
    });
  }

  // Render JSX element and insert data
  return (
    <div>
      <input placeholder="Enter Player's Name" value={''} onChange={() => ({ variables: { searchStr: 'Marcus Se' }})  }/>
      <br/>
      <h3>{name}</h3>
    </div>
  )
};

export default NameSearch;
