import React from "react";
import { gql } from 'apollo-boost';
import { useLazyQuery, useMutation, useApolloClient } from '@apollo/react-hooks';

const PLAYER_QUERY = gql`
  query PlayerId($searchStr: String!){
    getPlayer(searchStr: $searchStr){
      searchStr
      name
      id
    }
  }
`;

const PLAYER_MUTATION = gql`
  mutation setPlayer($searchStr: String!) {
    setPlayerId(searchStr: $searchStr)
  }
`;

// NameSearch function for taking text entered and searching for closest player
// whose name matches the text
function NameSearch(): any {
  // Initialize ApolloClient so we can get access to ApolloProvider's cache
  const client = useApolloClient();
  let cache = client.cache;

  const [getPlayer, { error, data }] = useLazyQuery(PLAYER_QUERY);

  // handle error state
  if (error) {
    console.warn(error);
    return (<div>`Error! ${error.message}`</div>);
  }

  //const [setPlayerId, { data }] = useMutation(PLAYER_MUTATION, {variables: { searchStr }});

  // Render JSX element and insert data
  return (
    <div>
      <input type="text" placeholder="Enter Player's Name" value={data ? data.searchStr : ''} onChange={e => {e.preventDefault(); getPlayer({ variables: { searchStr: e.target.value }})}}/>
      <br/>
      <h3>{data ? data.name : ''}</h3>
    </div>
  )
};

export default NameSearch;
