import React from "react";
import { gql } from 'apollo-boost';
import { useLazyQuery, useApolloClient } from '@apollo/react-hooks';

const PLAYER_QUERY = gql`
  query Player($searchStr: String!){
    getPlayer(searchStr: $searchStr){
      searchStr
      name
      id
    }
  }
`;

type playerQueryObj = {
  searchStr: string,
  name: string,
  id: number
};

// NameSearch function for taking text entered and searching for closest player
// whose name matches the text
function NameSearch(): any {
  // Initialize ApolloClient so we can get access to ApolloProvider's cache
  const client = useApolloClient();

  // Use useLazyQuery() hook to pass in query server
  const [getPlayer, { loading, error, data}] = useLazyQuery(PLAYER_QUERY);

  // handle return and error states
  if (loading) return (<div>'Loading...'</div>);
  if (error) {
    console.warn(error);
    return (<div>`Error! ${error.message}`</div>);
  }

  // Render JSX element and insert data
  return (
    <div>
      <input placeholder="Enter Player's Name" value={data ? data.getPlayer.searchStr : ''} onBlur={e => getPlayer({ variables: { searchStr: e.target.value }})  }/>
      <br/>
      <h3>{data ? data.getPlayer.name : ''}</h3>
    </div>
  )
};

export default NameSearch;
