import React from 'react';
import ApolloClient, { gql } from 'apollo-boost';
import { ApolloProvider } from '@apollo/react-hooks';
import { InMemoryCache } from 'apollo-cache-inmemory';
import NameSearch from './NameSearch';
//import SubmitSearch from './submitData';
//import { state, actionInterface, initialState } from '../state';
//import { reducer } from '../reducer';
import '../application.css'
declare let module: any;

// define context properties
/*interface IContextProps {
  state: state,
  dispatch: Dispatch<actionInterface>;
}*/

// instantiate context with properties
//const StateContext = createContext({} as IContextProps);

const typeDefs = gql`
  Player {
    searchStr: String
    name: String
    id: Number
  }
`;

// Instantiate cache and Apollo client
let cache = new InMemoryCache();
const client = new ApolloClient({
  uri: 'http://192.168.1.67:5000',
  cache: cache,
});

// Initialize cache data
cache.writeData({
  data: {
    searchStr: '',
    name: '',
    id: null
  },
});


// GraphQL query
