import React from "react";
import { gql } from 'apollo-boost';
import { useQuery, useLazyQuery, useApolloClient } from '@apollo/react-hooks';

const VORP_QUERY = gql`
  query PlayerVorp($playerId: Int!){
    getPlayerVorp(playerId: $playerId, startDate: "01/01/2018", endDate: "12/31/2018")
    {
      vorp
    }
  }
`;

const ID_QUERY = gql`
  {
    playerId @client
  }
`;

// helper function to validate string is in proper date format
function validateDate(testdate: string) {
    var date_regex = /^(0[1-9]|1[0-2])\/(0[1-9]|1\d|2\d|3[01])\/(19|20)\d{2}$/ ;
    return date_regex.test(testdate);
}

const SubmitSearch = (): any => {
  // connect to our context so that we can access state values
  const client = useApolloClient();
  let cache = client.cache;

  // Use refetch() in useQuery to trigger Query when Submit button pressed: https://www.apollographql.com/docs/react/data/queries/#refetching
  //const { loading, error, data, refetch } = useQuery(VORP_QUERY, {variables: {id: 11477}});
  const [calcPlayerVorp, { error, data }] = useLazyQuery(VORP_QUERY);

  function getVorp():void {
    console.log(cache);
    const data:any = cache.readQuery({query: ID_QUERY});
    console.log(data);

    calcPlayerVorp({variables: {playerId: 10155}})

    // Handle errors
    if (error) {
      console.warn(error);
      //return (<div>`Error! ${error.message}`</div>);
    }

    return;
  };

  // render component, connect date fields to relevant state props, set onchange
  // those fields to relevant function, and onclick for submit button to relevant
  // function.  Show rounded value of vorp property
  console.log(data);
  // Using id local field as variable for getPlayerVorp Query:  https://www.apollographql.com/docs/react/data/local-state/#using-client-fields-as-variables
  return (
    <div>
        <div> <h4>Start Date for Player:</h4> <br /> </div>
        <div> <input type="text" placeholder="MM/DD/YYYY" /></div>
        <div> <h4>End Date for Player: </h4><br /></div>
        <div> <input type="text" placeholder="MM/DD/YYYY" /></div>
        <div><input type="submit" value="Submit" name="submit" onClick={() => getVorp()}/></div>
        <br />
        <div><h3>VORP: { data ? data.getPlayerVorp.vorp : '' }</h3></div>
    </div>
  )
}

export default SubmitSearch;
