import React from "react";
import { gql } from 'apollo-boost';
import { useQuery, useApolloClient } from '@apollo/react-hooks';
import { typeDefs } from './BaseballApp';

/*const NameSearchProps = {
  context: Object
};*/

/*const PLAYER_QUERY = gql`
  {
    getPlayer(searchStr: "JT Real")
    {
      searchStr
      name
      id
    }
  }
`;*/

const PLAYER_QUERY = gql`
  query getPlayer($searchStr: String!){
    getPlayer(searchStr: "Bryce H"){
      searchStr
      name
      id
    }
  }
`;

// NameSearch function for taking text entered and searching for closest player
// whose name matches the text
function NameSearch(): any {
  const client = useApolloClient();
  const { loading, error, data } = useQuery(PLAYER_QUERY, {variables: { searchStr: 'Christian Yel'}});
  if (loading) return (<div>'Loading...'</div>);
  if (error) {
    console.warn(error);
    return (<div>`Error! ${error.message}`</div>);
  }

  let playerName:string = data.getPlayer.name;
  let textEntered:string = data.getPlayer.searchStr;

  client.writeData({
    data: {
      searchStr: textEntered,
      name: playerName,
      id: data.getPlayer.id,
    }
  });
  return (
    <div>
      <input placeholder="Enter Player's Name" value={textEntered} onChange={e=>e.preventDefault() }/>
      <br/>
      <h3>{playerName}</h3>
    </div>
  )
  /*return (
    <div>
      <input placeholder="Enter Player's Name" name="playerText" value={ data.getPlayer.searchStr } onChange={onPlayerTextEntered} />
      <br />
      <h3>{ data.getPlayer.name }</h3>
    </div>
  );*/
};

export default NameSearch;
