import React from "react";
import { gql } from 'apollo-boost';
import { useQuery } from '@apollo/react-hooks';

const PLAYER_QUERY = gql`
  query PlayerId($searchStr: String!){
    getPlayer(searchStr: $searchStr){
      name
      id
    }
  }
`;

type setPlayerQueryObj = {
  searchStr: string
};

export const resolvers = {
  Mutation: {
    setPlayerId: (_: Object, { searchStr }: setPlayerQueryObj, { cache }: any) => {
      const { error, data } = useQuery(PLAYER_QUERY);

      if (error) {
        console.warn(error);
        return (`Error! ${error.message}`);
      }

      if(data){
        cache.writeData({
          data: {
            searchStr: searchStr,
            name: data.getPlayer.name,
            id: data.getPlayer.id,
          }
        })
      }
      return { searchStr, name };
    }
  },

  Query: {
    getPlayerId: (_, { name }, { cache }) => {

    }
  }
};

export default resolvers;
