import React from "react";
import { gql } from 'apollo-boost';
import { useQuery } from '@apollo/react-hooks';
//import { baseballActions } from '../state';
//import { useStateValue } from './BaseballApp';
//let NAME_SEARCH_URL: string = "http://192.168.1.67:5000/player?str="

const PLAYER_QUERY = gql`
  {
    getPlayer(searchStr: String!){
      name
      id
    }
  }
`;

// NameSearch function for taking text entered and searching for closest player
// whose name matches the text
function NameSearch({onPlayerTextEntered}) {

  // https://github.com/apollographql/apollo-client/blob/version-2.6/docs/source/data/queries.mdx
  const { loading, error, data } = useQuery(PLAYER_QUERY);

  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;

  return (
    <div>
      <input placeholder="Enter Player's Name" name="playerText" value={ data.getPlayer.searchStr } onChange={onPlayerTextEntered} />
      <br />
      <h3>{ playerText.name }</h3>
    </div>
  );
};

export default NameSearch;
